# (FPCore
#  (x)
#  :name
#  "exp1x_log"
#  :description
#  "Generated by FPTaylor"
#  :precision
#  binary64
#  :pre
#  (<= 1/100 x 1/2)
#  (/ (- (exp x) 1) (log (exp x))))

using IntervalArithmetic
using DataStructure
using BenchTest

y = DataVariable("y","real",@interval(-Inf,+Inf))
x = DataVariable("x","real",@interval(1/100,1/2))
c1 = DataConstant("c1","int",1)
eps = DataConstant("eps","real",10^-10)

variables = [y, x]
constants = [c1]

C = DataConstraint("C_exp1x_log",
                   constants,
                   variables,
                   "=",
                   y,
                   DataBinaryOperation("/",
                                       DataBinaryOperation("-",
                                                           DataUnaryOperation("exp",
                                                                              x),
                                                           c1),
                                       DataUnaryOperation("log",
                                                           DataUnaryOperation("exp",x))),
                   eps)

test(C, variables, constants, ARGS)
